rot),\r\n		\'includeTVs\' => $includeTVs,\r\n		\'tvPrefix\' => \'\',\r\n		\'tpl\' => $tpl,\r\n		\'includeContent\' => $includeContent,\r\n		\'sortby\' => false,\r\n		\'sortdir\' => false,\r\n		\'return\' => $return,\r\n		\'tvFilters\' => implode(\',\', $filter),\r\n		\'limit\' => $limit ? 0 : $limit\r\n	);\r\n\r\n	$more = $count - $offset - $limit;\r\n	$lim = $more > $limit ? $limit : $more;\r\n\r\n	$button = \'\';\r\n	if($more > 0){\r\n		$button = \'<a id=\"load-more\" href=\"#\" class=\"btn-lg btn-primary\">Загрузить еще \'.$lim.\' из \'.$more.\'</a>\';\r\n	}\r\n\r\n	return $modx->runSnippet(\'pdoResources\', $params);', 0, 'a:0:{}', '', 1, 'core/elements/snippets/tarotFilter.php'),
