(62, 1, 0, 'encryptField', '', 0, 0, 0, 'require_once $_SERVER[\'DOCUMENT_ROOT\'] . \'/core/elements/snippets/profile/encrypt.php\';\n\nif ($modx->user->id) {\n	if ($user = $modx->getObject(\'modUser\', $modx->user->id)) {\n		if ($profile = $user->getOne(\'Profile\')) {\n			$extended = $profile->get(\'extended\');\n			$extended[\'cvc\'] = mc_encrypt($_POST[\'cvc\']);\n			$profile->set(\'extended\', $extended);\n			$profile->save();\n\n			if (!$profile->save()) {\n				$modx->log(xPDO::LOG_LEVEL_ERROR, \"Ошибка сохранения профиля\");\n			}\n		}\n	}\n}', 0, 'a:0:{}', '', 1, 'core/elements/snippets/encryptField.php');
